import React from "react";
import {useEffect, useState} from  'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { library } from '@fortawesome/fontawesome-svg-core'
import { faUniversity, faLaptopCode, faBriefcase, faProjectDiagram, faChalkboardTeacher } from '@fortawesome/free-solid-svg-icons'


function Header() {
    library.add(faUniversity, faLaptopCode, faBriefcase, faProjectDiagram, faChalkboardTeacher)
    let headings = [
        {name: "Work Experience", icon: ["fa", "university"]}, 
        {name: "Education", icon: ["fa", "laptop-code"]}, 
        {name: "Skills", icon: ["fa", "briefcase"]}, 
        {name: "Projects", icon: ["fa", "project-diagram"]},
        {name: "Leadership", icon: ["fa", "chalkboard-teacher"]},
    ]

    const [isNavOpen, openNav] = useState(false)
    const [itemSelected, selectItem] = useState('Home')
    const [pointDir, setPointDir] = useState('point-right')

    useEffect(() => {
        setTimeout(() => {
            if (isNavOpen) setPointDir('point-left')
            else setPointDir('point-right')
        }, 400)
      }, [isNavOpen])

    return (
        <div>
            <div className="font-mono">
                <div className= "flex flex-row w-full">
                    <div className="p-2">
                        <span>11001 - Steven</span>                     
                    </div>
                    <div className="flex flex-row ml-auto">
                    <div className={`${isNavOpen ? 'w-36' : 'w-16'} transition-all duration-200 ease-in-out bg-white dark:bg-darkModeGrey`} data-testid='side-nav-bar'>
                        <div className='flex flex-col h-[calc(100vh-77px)]'>
                        <div className='p-3'>
                            <div className='flex flex-col justify-center items-center ml-6'>
                            <button className = 'nav-wrapper nav-open' onClick={() => openNav(prev => !prev)} data-testid = "nav-open-button">
                                <div className="nav-menu">
                                    <span className={`${pointDir} lines bg-lighterGrey before:bg-lighterGrey after:bg-lighterGrey dark:bg-lighterGrey dark:before:bg-lighterGrey dark:after:bg-lighterGrey`}></span>
                                </div>
                            </button>
                            </div>
                            <hr className='mt-3 text-lighterGrey dark:text-lighterGrey'></hr>
                            <div>
                            {headings.map((heading) => (
                                <div className="flex mr-4 transition-all ease-in-out hover:scale-110 p-2 duration-300 hover:bg-slate-600 hover:rounded-md">
                                    <a href={"#" + heading.name.toLowerCase().replace(" ", "")}>
                                        <span className="mr-1">{heading.name}</span>
                                        <FontAwesomeIcon icon={heading.icon} />
                                    </a>                
                                </div>
                            ))
                            }            
                            </div>
                        </div>
                    </div>
                    </div>

                    </div>
                </div>          
            </div>
        </div>
    );
}
export default Header;
